import 'package:flutter/material.dart';// -------------------------// State// -------------------------class AccountState {  final dynamic user;  final Locale lang;  AccountState({    this.user,    this.lang = const Locale('ru'),  });  AccountState copyWith({    dynamic user,    Locale? lang,  }) {    return AccountState(      user: user ?? this.user,      lang: lang ?? this.lang,    );  }}// -------------------------// Actions// -------------------------// Добавить данные пользователяclass AddUserData {  final dynamic value;  AddUserData(this.value);}// Удалить данные пользователяclass RemoveUserData {}// Изменить языкclass ChangeLang {  final Locale value;  ChangeLang(this.value);}// -------------------------// Reducers// -------------------------AccountState accountReducers(AccountState state, action) {  switch (action.runtimeType) {    case AddUserData:      return state.copyWith(user: action.value);    case RemoveUserData:      return state.copyWith(user: null);    case ChangeLang:      return state.copyWith(lang: action.value);    default:      return state;  }}