import 'package:flutter/material.dart';// -------------------------// State// -------------------------class AccountState {  dynamic user;  Locale lang;  AccountState({    this.user,    this.lang = const Locale('ru'),  });}// -------------------------// Actions// -------------------------class AddUserData {  final dynamic value;  AddUserData(this.value);}class RemoveUserData {}class ChangeLang {  final Locale value;  ChangeLang(this.value);}// -------------------------// Reducers// -------------------------AccountState accountReducers(AccountState state, action) {  switch (action.runtimeType) {    case AddUserData:      state.user = action.value;      return state;    case RemoveUserData:      state.user = null;      return state;    case ChangeLang:      state.lang = action.value;      return state;    default:      return state;  }}